pragma solidity ^0.6.0;

import "@openzeppelin/contracts-ethereum-package/contracts/math/Math.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/Initializable.sol";

// Inheritance
import "./PausableUpgradeSafe.sol";
import "./OwnableUpgradeSafe.sol";

contract Gallery is Initializable, PausableUpgradeSafe, OwnableUpgradeSafe {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== CONSTANTS ========== */

    uint256 constant PROPOSAL_DAYS = 4;
    uint256 constant FIGURE_DAYS = 5;
    uint256 constant WINNING_PROPOSAL_COUNT = 10;
    uint256 constant WINNING_FIGURE_COUNT = 3;
    uint256 constant MIN_PROPOSAL_VOTES = 50 ether;
    uint256 constant MIN_FIGURE_VOTES = 10 ether;
    uint256 constant REWARDS_PER_PROPOSAL = 400 ether;
    uint256 constant AUTHOR_REWARDS_RATIO = 0.1 ether;
    uint256 constant NULL = 2**256 - 1;
    address constant DEAD_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    /* ========== STRUCTS ========== */

    struct Proposal {
        address proposer;
        string name;
        string info;
        uint256 createAt;
    }

    struct Figure {
        uint256 proposalId;
        address author;
        string info;
        uint256 createAt;
    }

    /* ========== STATE VARIABLES ========== */

    address public rewardsToken;
    address public stakingToken;
    uint256 public startAt;

    // array of all proposals
    Proposal[] public proposals;
    // array of proposal id of top proposal
    uint256[] public topProposals;
    // if proposal rewards is burned
    bool public proposalRewardsBurned;
    // total votes of all proposals
    uint256 public totalProposalVotes;
    // proposal id => total votes of this proposal
    mapping(uint256 => uint256) public proposalVotes;
    // account => array of proposal id
    mapping(address => uint256[]) public myProposals;
    // account => total votes of my proposals
    mapping(address => uint256) public myProposalVotes;
    // account => last update at
    mapping(address => uint256) public myProposalUpdateAt;
    // account => if my votes and rewards of proposals is withdrawn
    mapping(address => bool) public myProposalWithdrawn;
    // account => if my rewards of proposals is claimed
    mapping(address => bool) public myProposalClaimed;
    // account => proposal id => votes
    mapping(address => mapping(uint256 => uint256)) public mySingleProposalVotes;

    // array of all proposals
    Figure[] public figures;
    // top proposal id => array of figure id of top figure
    mapping(uint256 => uint256[]) public topFigures;
    // array of top figure rewards
    uint256[WINNING_FIGURE_COUNT] public topFigureRewards;
    // proposal id => array of figure id
    mapping(uint256 => uint256[]) public figuresOfProposal;
    // total votes of all figures
    uint256 public totalFigureVotes;
    // figure id => total votes of this figure
    mapping(uint256 => uint256) public figureVotes;
    // account => array of figure id
    mapping(address => uint256[]) public myFigures;
    // account => total votes of my figures
    mapping(address => uint256) public myFigureVotes;
    // account => last update at
    mapping(address => uint256) public myFigureUpdateAt;
    // account => if my votes of figures is withdrawn
    mapping(address => bool) public myFigureWithdrawn;
    // account => if my rewards of figures is claimed
    mapping(address => bool) public myFigureClaimed;
    // account => figure id => votes
    mapping(address => mapping(uint256 => uint256)) public mySingleFigureVotes;

    /* ========== CONSTRUCTOR ========== */

    function initialize(
        address _rewardsToken,
        address _stakingToken
    )  public initializer {
        super.__Pausable_init();
        super.__Ownable_init();
        rewardsToken = _rewardsToken;
        stakingToken = _stakingToken;
        topFigureRewards = [500 ether, 300 ether, 200 ether];
        topProposals = [NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL];
        startAt = block.timestamp;
    }

    /* ========== VIEWS ========== */

    function dayAt() public view returns (uint256) {
        return block.timestamp.sub(startAt).div(1 days);
    }

    function proposalCount() public view returns (uint256) {
        return proposals.length;
    }

    function figureCount() public view returns (uint256) {
        return figures.length;
    }

    function getTopProposal(uint256 index) public view returns (uint256) {
        return topProposals[index];
    }

    function getTopFigure(uint256 proposalId, uint256 index) public view returns (uint256) {
        if (topFigures[proposalId].length == 0) {
            return NULL;
        }
        return topFigures[proposalId][index];
    }

    function myProposalCount(address target) public view returns (uint256) {
        return myProposals[target].length;
    }

    function myFigureCount(address target) public view returns (uint256) {
        return myFigures[target].length;
    }

    function figuresOfProposalCount(uint256 proposalId) public view returns (uint256) {
        return figuresOfProposal[proposalId].length;
    }

    function myProposalRewards(address target) public view returns (uint256) {
        if (dayAt() < PROPOSAL_DAYS) {
            return 0;
        }
        return _calculateProposalRewards(target);
    }

    function myFigureRewards(address target) public view returns (uint256) {
        if (dayAt() < PROPOSAL_DAYS + FIGURE_DAYS) {
            return 0;
        }
        return _calculateFigureRewards(target);
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function createProposal(string calldata name, string calldata info) external whenNotPaused {
        require(dayAt() < PROPOSAL_DAYS, "Expired");
        require(bytes(name).length <= 32, "Name's length is too big");
        require(bytes(info).length <= 200, "Info's length is too big");

        // create proposal
        Proposal memory proposal;
        proposal.proposer = msg.sender;
        proposal.name = name;
        proposal.info = info;
        proposal.createAt = block.timestamp;
        proposals.push(proposal);

        // vote for proposal
        uint256 proposalId = proposals.length - 1;
        uint256 votes = MIN_PROPOSAL_VOTES;
        _voteForProposal(proposalId, votes);

        emit ProposalCreated(proposalId, votes);
    }

    function voteForProposal(uint256 proposalId, uint256 votes) external whenNotPaused {
        require(dayAt() < PROPOSAL_DAYS, "Expired");
        require(votes > 0, "Votes should larger then zero");
        require(proposalId < proposals.length, "Invalid proposal id");

        // vote for proposal
        _voteForProposal(proposalId, votes);

        emit ProposalVoted(proposalId, votes);
    }

    function withdrawFromProposal() external whenNotPaused {
        require(dayAt() >= PROPOSAL_DAYS, "Not expired");
        require(myProposalUpdateAt[msg.sender] != 0, "Cannot withdraw from proposal");
        require(block.timestamp.sub(myProposalUpdateAt[msg.sender]) > 4 days, "Cannot withdraw within 4 days");
        require(!myProposalWithdrawn[msg.sender], "You have already withdrawn");
        myProposalWithdrawn[msg.sender] = true;

        // withdraw votes
        uint256 votes = myProposalVotes[msg.sender];
        if (votes > 0) {
            IERC20(stakingToken).safeTransfer(msg.sender, votes);
        }

        emit ProposalWithdrawn(votes);
    }

    function claimFromProposal() external whenNotPaused {
        require(dayAt() >= PROPOSAL_DAYS, "Not expired");
        require(myProposalUpdateAt[msg.sender] != 0, "Cannot claim from proposal");
        require(!myProposalClaimed[msg.sender], "You have already claimed");
        myProposalClaimed[msg.sender] = true;

        // claim rewards
        uint256 rewards = _calculateProposalRewards(msg.sender);
        if (rewards > 0) {
            IERC20(rewardsToken).safeTransfer(msg.sender, rewards);
        }

        emit ProposalClaimed(rewards);
    }

    function burnFromProposal() external whenNotPaused onlyOwner {
        require(dayAt() >= PROPOSAL_DAYS, "Not expired");
        require(!proposalRewardsBurned, "Proposal rewards has been burned");
        proposalRewardsBurned = true;

        uint256 burnCount = 0;
        for (uint256 i = 0; i < WINNING_PROPOSAL_COUNT; i++) {
            if (topProposals[i] == NULL) {
                burnCount++;
            }
        }
        uint256 burnAmount = 0;
        if (burnCount > 0) {
            burnAmount = REWARDS_PER_PROPOSAL.mul(burnCount);
            IERC20(rewardsToken).safeTransfer(DEAD_ADDRESS, burnAmount);
        }

        emit ProposalBurned(burnAmount);
    }

    function createFigure(uint256 proposalId, string calldata info) external whenNotPaused {
        require(dayAt() < PROPOSAL_DAYS + FIGURE_DAYS, "Expired");
        require(proposalId < proposals.length, "Invalid proposal id");
        require(bytes(info).length <= 500, "Info's length is too big");
        bool inTopProposals = false;
        for (uint256 i = 0; i < WINNING_PROPOSAL_COUNT; i++) {
            if (topProposals[i] == proposalId) {
                inTopProposals = true;
                break;
            }
        }
        if (!inTopProposals) {
            revert("Invalid proposal id");
        }

        if (topFigures[proposalId].length != WINNING_FIGURE_COUNT) {
            topFigures[proposalId] = [NULL, NULL, NULL];
        }

        // create figure
        Figure memory figure;
        figure.proposalId = proposalId;
        figure.author = msg.sender;
        figure.info = info;
        figure.createAt = block.timestamp;
        figures.push(figure);

        // vote for figure
        uint256 figureId = figures.length - 1;
        uint256 votes = MIN_FIGURE_VOTES;
        _voteForFigure(figureId, votes);
        figuresOfProposal[proposalId].push(figureId);

        emit FigureCreated(figureId, votes);
    }

    function voteForFigure(uint256 figureId, uint256 votes) external whenNotPaused {
        require(dayAt() < PROPOSAL_DAYS + FIGURE_DAYS, "Expired");
        require(figureId < figures.length, "Invalid figure id");
        require(votes > 0, "Votes should larger then zero");

        // vote for figure
        _voteForFigure(figureId, votes);

        emit FigureVoted(figureId, votes);
    }

    function withdrawFromFigure() external whenNotPaused {
        require(dayAt() >= PROPOSAL_DAYS + FIGURE_DAYS, "Not expired");
        require(myFigureUpdateAt[msg.sender] != 0, "Cannot withdraw from figure");
        require(block.timestamp.sub(myFigureUpdateAt[msg.sender]) > 7 days, "Cannot claim within 7 days");
        require(!myFigureWithdrawn[msg.sender], "You have already withdrawn");
        myFigureWithdrawn[msg.sender] = true;

        // withdraw votes
        uint256 votes = myFigureVotes[msg.sender];
        if (votes > 0) {
            IERC20(stakingToken).safeTransfer(msg.sender, votes);
        }

        emit FigureWithdrawn(votes);
    }

    function claimFromFigure() external whenNotPaused {
        require(dayAt() >= PROPOSAL_DAYS + FIGURE_DAYS, "Not expired");
        require(myFigureUpdateAt[msg.sender] != 0, "Cannot claim from figure");
        require(!myFigureClaimed[msg.sender], "You have already claimed");
        myFigureClaimed[msg.sender] = true;

        // claim rewards
        uint256 rewards = _calculateFigureRewards(msg.sender);
        if (rewards > 0) {
            IERC20(rewardsToken).safeTransfer(msg.sender, rewards);
        }

        emit FigureClaimed(rewards);
    }

    function pause() external onlyOwner {
        super._pause();
    }

    function unpause() external onlyOwner {
        super._unpause();
    }

    function _voteForProposal(uint256 proposalId, uint256 votes) private {
        // send token to contract
        IERC20(stakingToken).safeTransferFrom(msg.sender, address(this), votes);

        // update votes
        totalProposalVotes = totalProposalVotes.add(votes);
        proposalVotes[proposalId] = proposalVotes[proposalId].add(votes);
        myProposalVotes[msg.sender] = myProposalVotes[msg.sender].add(votes);
        if (mySingleProposalVotes[msg.sender][proposalId] == 0) {
            myProposals[msg.sender].push(proposalId);
        }
        mySingleProposalVotes[msg.sender][proposalId] = mySingleProposalVotes[msg.sender][proposalId].add(votes);

        // sort top proposals
        _sortTopProposals(proposalId);

        // update vote time
        myProposalUpdateAt[msg.sender] = block.timestamp;
    }

    function _voteForFigure(uint256 figureId, uint256 votes) private {
        // send token to contract
        IERC20(stakingToken).safeTransferFrom(msg.sender, address(this), votes);

        // update votes
        totalFigureVotes = totalFigureVotes.add(votes);
        figureVotes[figureId] = figureVotes[figureId].add(votes);
        myFigureVotes[msg.sender] = myFigureVotes[msg.sender].add(votes);
        if (mySingleFigureVotes[msg.sender][figureId] == 0) {
            myFigures[msg.sender].push(figureId);
        }
        mySingleFigureVotes[msg.sender][figureId] = mySingleFigureVotes[msg.sender][figureId].add(votes);

        // sort top figures
        uint256 proposalId = figures[figureId].proposalId;
        _sortTopFigures(proposalId, figureId);

        // update vote time
        myFigureUpdateAt[msg.sender] = block.timestamp;
    }

    function _sortTopProposals(uint256 proposalId) private {
        uint256 begin = NULL;
        uint256 end = topProposals.length-1;
        for (uint256 i = 0; i <= end; i++) {
            uint256 _proposalId = topProposals[i];
            if (_proposalId == NULL) {
                if (begin == NULL) {
                    begin = i;
                }
                end = i;
                break;
            }
            if (proposalId == _proposalId) {
                if (begin == NULL) {
                    begin = i;
                }
                end = i;
                break;
            }
            if (proposalVotes[proposalId] > proposalVotes[_proposalId]) {
                if (begin == NULL) {
                    begin = i;
                }
            }
        }
        // right shift array elements
        for (uint256 j = end; j > begin; j--) {
            topProposals[j] = topProposals[j-1];
        }
        if (begin != NULL) {
            topProposals[begin] = proposalId;
        }
    }

    function _sortTopFigures(uint256 proposalId, uint256 figureId) private {
        uint256 begin = NULL;
        uint256 end = topFigures[proposalId].length-1;
        for (uint256 i = 0; i <= end; i++) {
            uint256 _figureId = topFigures[proposalId][i];
            if (_figureId == NULL) {
                if (begin == NULL) {
                    begin = i;
                }
                end = i;
                break;
            }
            if (figureId == _figureId) {
                if (begin == NULL) {
                    begin = i;
                }
                end = i;
                break;
            }
            if (figureVotes[figureId] > figureVotes[_figureId]) {
                if (begin == NULL) {
                    begin = i;
                }
            }
        }
        // right shift array elements
        for (uint256 j = end; j > begin; j--) {
            topFigures[proposalId][j] = topFigures[proposalId][j-1];
        }
        if (begin != NULL) {
            topFigures[proposalId][begin] = figureId;
        }
    }

    function _calculateProposalRewards(address target) private view returns(uint256) {
        uint256 rewards = 0;
        for (uint256 i = 0; i < topProposals.length; i++) {
            if (topProposals[i] == NULL) {
                break;
            }
            uint256 proposalId = topProposals[i];
            uint256 myVotes = mySingleProposalVotes[target][proposalId];
            if (myVotes > 0) {
                uint256 totalVotes = proposalVotes[proposalId];
                rewards = rewards.add(REWARDS_PER_PROPOSAL.mul(myVotes).div(totalVotes));
            }
        }
        return rewards;
    }

    function _calculateFigureRewards(address target) private view returns(uint256) {
        uint256 rewards = 0;
        for (uint256 i = 0; i < WINNING_PROPOSAL_COUNT; i++) {
            uint256 proposalId = topProposals[i];
            if (proposalId == NULL) {
                break;
            }
            if (topFigures[proposalId].length == 0) {
                continue;
            }
            for (uint256 j = 0; j < WINNING_FIGURE_COUNT; j++) {
                uint256 figureId = topFigures[proposalId][j];
                if (figureId == NULL) {
                    break;
                }
                uint256 myVotes = mySingleFigureVotes[target][figureId];
                if (myVotes > 0) {
                    uint256 totalVotes = figureVotes[figureId];
                    uint256 authorRewards = topFigureRewards[j].mul(AUTHOR_REWARDS_RATIO).div(1 ether);
                    uint256 totalRewards = topFigureRewards[j].sub(authorRewards);
                    rewards = rewards.add(totalRewards.mul(myVotes).div(totalVotes));
                    // is author
                    if (target == figures[figureId].author) {
                        rewards = rewards.add(authorRewards);
                    }
                }
            }
        }
        return rewards;
    }

    /* ========== EVENTS ========== */

    event ProposalCreated(uint256 indexed proposalId, uint256 votes);
    event ProposalVoted(uint256 indexed proposalId, uint256 votes);
    event ProposalWithdrawn(uint256 votes);
    event ProposalClaimed(uint256 rewards);
    event ProposalBurned(uint256 votes);
    event FigureCreated(uint256 indexed figureId, uint256 votes);
    event FigureVoted(uint256 indexed figureId, uint256 votes);
    event FigureWithdrawn(uint256 votes);
    event FigureClaimed(uint256 rewards);
}
