pragma solidity ^0.6.0;

import "@openzeppelin/contracts-ethereum-package/contracts/math/Math.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/Initializable.sol";

// Inheritance
import "./interfaces/IStakingRewards.sol";
import "./interfaces/IUniswapPriceOracle.sol";
import "./ReentrancyGuardUpgradeSafe.sol";
import "./PausableUpgradeSafe.sol";

contract StakingRewardsV2 is IStakingRewards,
    Initializable,
    ReentrancyGuardUpgradeSafe,
    PausableUpgradeSafe
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== CONSTANTS ========== */

    uint256 constant STAKING_DAYS = 14;
    uint256 constant BURN_RATIO_DAYS = 11;
    address constant DEAD_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    /* ========== STATE VARIABLES ========== */

    address public rewardsToken;
    address public stakingToken;
    address public oracleAddress;
    uint256 public lastUpdateTime;
    uint256 public startAt;
    uint256[STAKING_DAYS] public dailyRewards;
    uint256[BURN_RATIO_DAYS] public burnRatios;
    mapping(address => uint256) public lastUpdateAt;
    mapping(address => uint256) public rewards;

    uint256 private _totalBurned;
    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;

    /* ========== CONSTRUCTOR ========== */

    function initialize(
        address _rewardsToken,
        address _stakingToken,
        address _oracleAddress,
        uint256[STAKING_DAYS] memory _dailyRewards,
        uint256[BURN_RATIO_DAYS] memory _burnRatios
    )  public initializer {
        super.__ReentrancyGuard_init();
        super.__Pausable_init();
        rewardsToken = _rewardsToken;
        stakingToken = _stakingToken;
        oracleAddress = _oracleAddress;
        startAt = block.timestamp;
        dailyRewards = _dailyRewards;
        burnRatios = _burnRatios;
    }

    /* ========== VIEWS ========== */

    function dayAt() public view returns (uint256) {
        return block.timestamp.sub(startAt).div(1 days);
    }

    function totalBurned() external view returns (uint256) {
        return _totalBurned;
    }

    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) external view override returns (uint256) {
        return _balances[account];
    }

    // Not used
    function lastTimeRewardApplicable() public view override returns (uint256) {
        return 0;
    }

    // Not used
    function rewardPerToken() public view override returns (uint256) {
        return 0;
    }

    function earned(address account) public view override returns (uint256) {
        uint256[] memory _earnedList = _calculateEarned(account);
        uint256 _totalEarned = 0;
        for (uint256 i = 0; i < _earnedList.length; i++) {
            _totalEarned = _totalEarned.add(_earnedList[i]);
        }

        return _totalEarned;
   }

    // Not used
    function getRewardForDuration() external override view returns (uint256) {
        return 0;
    }

    function apy() external override view returns (uint256) {
        uint256 rewardsTokenPrice = price(rewardsToken);
        uint256 stakingTokenPrice = price(stakingToken);
        uint256 rewardsTokenAmount = getTotalRewards();
        uint256 stakeTokenAmount = _totalSupply;

        return (rewardsTokenPrice.mul(rewardsTokenAmount))
            .div((stakingTokenPrice.mul(stakeTokenAmount)));
    }

    // Not used
    function getSteakPercentage() external override view returns (uint256) {
        return 0;
    }

    function price(address token) public view returns (uint256) {
        return IUniswapPriceOracle(oracleAddress).consult(token, 1 ether);
    }

    function getTotalRewards() public view returns (uint256) {
        uint256 _count = Math.min(dayAt()+1, STAKING_DAYS);
        uint256 _totalRewards = 0;
        for (uint256 i = 0; i < _count; i++) {
            _totalRewards = _totalRewards.add(dailyRewards[i]);
        }

        return _totalRewards;
    }

    function burned(uint256 reward, address account) public view returns (uint256) {
        uint256 _dayAt = Math.min(
            block.timestamp.sub(lastUpdateAt[account]).div(1 days),
            BURN_RATIO_DAYS - 1
        );

        return reward.mul(burnRatios[_dayAt]).div(1 ether);
    }

    function _calculateEarned(address account) private view returns (uint256[] memory) {
        uint256[] memory _earnedList = new uint256[](STAKING_DAYS);
        if (_totalSupply == 0) {
            return _earnedList;
        }
        uint256 _lastUpdateAt = lastUpdateAt[account] == 0 ? block.timestamp : lastUpdateAt[account];
        for (uint256 i = 0; i < STAKING_DAYS; i++) {
            uint256 _endAt = startAt.add(i.mul(1 days)).add(1 days);
            uint256 _span = 0;
            if (block.timestamp > _endAt) {
                if (_lastUpdateAt < _endAt) {
                    _span = _endAt.sub(_lastUpdateAt);
                    _lastUpdateAt = _endAt;
                }
            } else {
                if (_lastUpdateAt < block.timestamp) {
                    _span = block.timestamp.sub(_lastUpdateAt);
                    _lastUpdateAt = block.timestamp;
                }
            }
            if (_span > 0) {
                _earnedList[i] = dailyRewards[i]
                .mul(_balances[account])
                .div(_totalSupply)
                .mul(_span)
                .div(1 days);
            }
        }

        return _earnedList;
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function stake(uint256 amount) external override nonReentrant whenNotPaused updateReward {
        require(amount > 0, "Cannot stake 0");
        require(dayAt() < STAKING_DAYS, "Cannot stake over 14 days");
        _totalSupply = _totalSupply.add(amount);
        _balances[msg.sender] = _balances[msg.sender].add(amount);
        IERC20(stakingToken).safeTransferFrom(msg.sender, address(this), amount);
        emit Staked(msg.sender, amount);
        lastUpdateAt[msg.sender] = block.timestamp;
    }

    function withdraw(uint256 amount) public override nonReentrant updateReward {
        require(amount > 0, "Cannot withdraw 0");
        _totalSupply = _totalSupply.sub(amount);
        _balances[msg.sender] = _balances[msg.sender].sub(amount);
        IERC20(stakingToken).safeTransfer(msg.sender, amount);
        emit Withdrawn(msg.sender, amount);
        lastUpdateAt[msg.sender] = block.timestamp;
    }

    function getReward() public override nonReentrant updateReward {
        uint256 reward = rewards[msg.sender];
        if (reward > 0) {
            rewards[msg.sender] = 0;
            uint256 _burnedReward = burned(reward, msg.sender);
            uint256 _actualReward = reward.sub(_burnedReward);
            if (_actualReward > 0) {
                IERC20(rewardsToken).safeTransfer(msg.sender, _actualReward);
            }
            if (_burnedReward > 0) {
                _totalBurned = _totalBurned.add(_burnedReward);
                IERC20(rewardsToken).safeTransfer(DEAD_ADDRESS, _burnedReward);
            }
        }
        emit RewardPaid(msg.sender, reward);
        lastUpdateAt[msg.sender] = block.timestamp;
    }

    function exit() external override {
        withdraw(_balances[msg.sender]);
        getReward();
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward() {
        address _account = msg.sender;
        uint256[] memory _earnedList = _calculateEarned(_account);
        for (uint256 i = 0; i < _earnedList.length; i++) {
            uint256 _earned = _earnedList[i];
            if (_earned > 0) {
                rewards[_account] = rewards[_account].add(_earned);
                dailyRewards[i] = dailyRewards[i].sub(_earned);
            }
        }
        _;
    }

    /* ========== EVENTS ========== */

    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
}
